import React, { useState } from "react";
import Calendar from "react-calendar";


function App() {
  const [value, setValue] = useState(new Date());

  function onChange(nextValue) {
    setValue(nextValue);
    // this is allowing to navigate the calendar by clicking eg. a day from a different month
  }

  function onClickDay() {
    // point to the DatePage component
  }

  return (
    <Calendar
      // FUNCTIONS
      onChange={onChange}
      onClickDay={onClickDay}
      value={value}

      // SETTINGS
      tileContent={"https://media4.giphy.com/media/l0Iych4GHWMRxci2I/giphy.gif?cid=790b761194122cfca4d97229dc5a95369c32bf677d08d9ad&rid=giphy.gif&ct=g"}
      minDetail="month"
      maxDate={new Date()}
    />
  );

  // return <h1>
  //   Konnichiwa Worlds
    
  // </h1>
}

export default App
